import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/api';

const apiService = {
  getPessoas: () => axios.get(`${API_URL}/pessoas/`),
  createPessoa: (pessoa) => axios.post(`${API_URL}/pessoas/`, pessoa),
  updatePessoa: (id, pessoa) => axios.put(`${API_URL}/pessoas/${id}/`, pessoa),
  deletePessoa: (id) => axios.delete(`${API_URL}/pessoas/${id}/`),
  calculateIdealWeight: (id) => axios.get(`${API_URL}/pessoas/${id}/calcular-peso-ideal/`)
};

export default apiService;
