import React, { useEffect, useState } from 'react';
import apiService from '../services/apiService';

const PessoaList = () => {
  const [pessoas, setPessoas] = useState([]);
  const [selectedPessoa, setSelectedPessoa] = useState(null);

  useEffect(() => {
    fetchPessoas();
  }, []);

  const fetchPessoas = () => {
    apiService.getPessoas().then((response) => {
      setPessoas(response.data);
    });
  };

  const handleDelete = (id) => {
    apiService.deletePessoa(id).then(() => {
      fetchPessoas();
    });
  };

  const handleEdit = (pessoa) => {
    setSelectedPessoa(pessoa);
  };

  const handleCalculateIdealWeight = (id) => {
    apiService.calculateIdealWeight(id).then((response) => {
      alert(`Peso ideal: ${response.data.pesoIdeal}`);
    });
  };

  return (
    <div>
      <h2>Lista de Pessoas</h2>
      <PessoaForm onPessoaAdded={fetchPessoas} onPessoaUpdated={fetchPessoas} pessoa={selectedPessoa} />
      <ul>
        {pessoas.map((pessoa) => (
          <li key={pessoa.id}>
            {pessoa.nome} - {pessoa.altura}m - {pessoa.peso}kg - {pessoa.sexo}
            <button onClick={() => handleEdit(pessoa)}>Editar</button>
            <button onClick={() => handleDelete(pessoa.id)}>Excluir</button>
            <button onClick={() => handleCalculateIdealWeight(pessoa.id)}>Calcular Peso Ideal</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PessoaList;
